def main():
    try:
        conn = psycopg2.connect(DB_URL)
        cur = conn.cursor()

        # Insertar Q&A en la tabla existente
        for q, a in qa_pairs:
            cur.execute(
                """
                INSERT INTO golden_dataset (question, ground_truth, created_at)
                VALUES (%s, %s, %s);
                """,
                (q, a, datetime.utcnow())
            )

        conn.commit()
        cur.close()
        conn.close()
        print("✅ Dataset insertado correctamente en Railway!")

    except Exception as e:
        print("❌ Error:", e)

if __name__ == "__main__":
    main()